import React, { useState } from 'react';
import { caculatewinner } from '../../hepper';
import Board from './Board';
import "./GameStyle.css"
const Game = () => {
    // const [board, setBoard] = useState(Array(9).fill(null))
    // const [xIsNext, setXIsNext] = useState(true)
    const [state , setState] = useState({
        xIsNext: true,
        board: Array(9).fill(null),
    })
    const winner = caculatewinner(setBoard.board);
    const handleClick = (index)=>{
        const boardCopy = [...setBoard.board]
        if(winner || boardCopy[index]){
            return
        }
        boardCopy[index] = setXIsNext.xIsNext ? "X" : "O";
        setBoard(boardCopy)
        setXIsNext(!setXIsNext.xIsNext)
    }
    const handleReset = ()=>{
        setBoard(Array(9).fill(null))
        setXIsNext(true)
    }
    return (
        <div>
            <Board cells={setBoard.board} onClick={handleClick}></Board>
            <button onClick={handleReset}>Reset game</button>
            <div>{winner ? `Winner is ${winner || ""}` : ""}</div>
        </div>
    );
};

export default Game;
